import React, { Component } from "react";
class Counter extends Component {
  state = {
    tags: ["tag1", "tag2", "tag3", "sss"],
  };
  renderTags() {
    return <>
    <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>
    <button onClick={() => {
      const reversedTags = Array.from(this.state.tags);
      reversedTags.reverse();
      this.setState(s => ({...this.state, tags: reversedTags }))
    }}>click me!</button>
    </>

  }

  render() {
    return <div>{this.renderTags()}</div>;
  }
